script:
//-------------------------------------------------------------
// Load ApexCharts if not already loaded
//-------------------------------------------------------------
function load_apexcharts(callback) {
    if (typeof ApexCharts !== "undefined") {
        callback();
        return;
    }
    const script = document.createElement("script");
    script.src = "https://cdn.jsdelivr.net/npm/apexcharts@3.45.0";
    script.onload = callback;
    document.head.appendChild(script);
}

//-------------------------------------------------------------
// EVENT READINESS FORM SCRIPT
//-------------------------------------------------------------
frappe.ui.form.on('Event Readiness', {
    refresh(frm) {
        if (frm.is_new()) {
            frm.fields_dict.custom_event_readiness_gauge.$wrapper.empty();
            frm.fields_dict.custom_tasks_overview.$wrapper.empty();
            return;
        }

        load_apexcharts(() => frm.trigger("render_gauge"));
        frm.trigger("render_tasks");
    },

    //---------------------------------------------------------
    // READINESS GAUGE + COUNTDOWN + SUMMARY
    //---------------------------------------------------------
    render_gauge(frm) {
        if (!frm.fields_dict.custom_event_readiness_gauge) return;

        const readiness = frm.doc.event_readiness || 0;
        let gauge_color = '#dc3545';
        if (readiness >= 70) gauge_color = '#28a745';
        else if (readiness >= 40) gauge_color = '#ffc107';

        const total     = frm.doc.total_tasks || 0;
        const pending   = frm.doc.pending_tasks || 0;
        const progress  = frm.doc.in_progress_tasks || 0;
        const completed = frm.doc.completed_tasks || 0;
        const delayed   = frm.doc.delayed_tasks || 0;

        frm.fields_dict.custom_event_readiness_gauge.$wrapper.html(`
            <div style="display:flex; gap:40px; justify-content:center; margin-top:10px;">

                <!-- LEFT PANEL -->
                <div style="flex:1; text-align:center;">
                    <h4 style="margin-bottom:10px;">Event Readiness: ${readiness}%</h4>

                    <!-- COUNTDOWN -->
                    <div id="event-countdown"
                         style="margin-bottom:12px; font-size:16px; font-weight:bold; color:#444;">
                        Calculating...
                    </div>

                    <!-- GAUGE -->
                    <div id="event_readiness_gauge" style="height:260px;"></div>
                </div>

                <!-- RIGHT PANEL -->
                <div style="flex:1;">
                    <h4>Task Summary</h4>
                    <div style="display:grid; grid-template-columns:repeat(2,1fr); gap:10px;">
                        <div class="stat-card"><b>Total Tasks</b><br>${total}</div>
                        <div class="stat-card" style="color:#0dcaf0;"><b>Pending</b><br>${pending}</div>
                        <div class="stat-card" style="color:#ffc107;"><b>In Progress</b><br>${progress}</div>
                        <div class="stat-card" style="color:#28a745;"><b>Completed</b><br>${completed}</div>
                        <div class="stat-card" style="color:#dc3545;"><b>Delayed</b><br>${delayed}</div>
                    </div>
                </div>
            </div>
        `);

        // RENDER GAUGE
        new ApexCharts(document.querySelector("#event_readiness_gauge"), {
            series: [readiness],
            chart: { type: 'radialBar', height: 260 },
            colors: [gauge_color],
            labels: ['Readiness'],
            plotOptions: {
                radialBar: {
                    hollow: { size: "60%" },
                    dataLabels: { value: { formatter: () => readiness + "%" } }
                }
            }
        }).render();

        //-----------------------------------------------------
        // SIMPLE COUNTDOWN (AFTER WRAPPER EXISTS)
        //-----------------------------------------------------
        setTimeout(() => {
            const eventDate = frm.doc.event_date;
            const el = document.getElementById("event-countdown");
            if (!eventDate || !el) return;

            function updateCountdown() {
                const now = new Date();
                const target = new Date(eventDate + " 00:00:00");
                const diff = target - now;

                if (diff <= 0) {
                    el.innerHTML = `<span style="color:#28a745;font-weight:bold;">üéâ Event Started</span>`;
                    return;
                }

                const days = Math.floor(diff / (1000 * 60 * 60 * 24));
                const hours = Math.floor((diff / (1000 * 60 * 60)) % 24);
                const mins = Math.floor((diff / (1000 * 60)) % 60);
                const secs = Math.floor((diff / 1000) % 60);

                el.innerHTML = `‚è≥ <b>${days}</b>d | <b>${hours}</b>h | <b>${mins}</b>m | <b>${secs}</b>s left`;
            }

            updateCountdown();
            setInterval(updateCountdown, 1000);
        }, 900);
    },

    //---------------------------------------------------------
    // TASKS TABLE WITH PERMISSION-BASED CONTROLS
    //---------------------------------------------------------
    render_tasks(frm) {
        frappe.call({
            method: "psn_custom_rdb_app.psn_readiness_dashboard.event_logic.get_tasks_for_event",
            args: { event_name: frm.doc.name },
            callback(r) {
                const data = r.message;
                const tasks = data.tasks || [];
                const current_user = data.user;
                const user_sector = data.user_sector;
                const is_lead = data.is_lead === 1;

                let filter_status = "", filter_sector = "", filter_incharge = "", sort_field = "creation", sort_order = "asc";

                function applyFiltersAndRender() {
                    let result = tasks;
                    if (filter_sector)   result = result.filter(t => t.sector === filter_sector);
                    if (filter_status)   result = result.filter(t => t.status === filter_status);
                    if (filter_incharge) result = result.filter(t => t.incharge === filter_incharge);

                    result.sort((a, b) => (a[sort_field] < b[sort_field] ? -1 : 1) * (sort_order === "asc" ? 1 : -1));
                    renderTable(result);
                }

                window.sortBy = field => {
                    sort_field = field;
                    sort_order = sort_order === "asc" ? "desc" : "asc";
                    applyFiltersAndRender();
                };

                function renderTable(list) {
                    let html = `
                        <div style="display:flex; justify-content:space-between; margin-bottom:12px;">
                            <div style="display:flex; gap:10px;">
                                <select id="flt-status" class="form-control" style="width:140px">
                                    <option value="">All Status</option>
                                    <option>Pending</option>
                                    <option>In Progress</option>
                                    <option>Completed</option>
                                    <option>Delayed</option>
                                </select>

                                <select id="flt-sector" class="form-control" style="width:140px">
                                    <option value="">All Sectors</option>
                                    ${[...new Set(tasks.map(t => t.sector))].map(sec => `<option>${sec}</option>`).join("")}
                                </select>

                                <select id="flt-incharge" class="form-control" style="width:140px">
                                    <option value="">All Users</option>
                                    ${[...new Set(tasks.map(t => t.incharge))].filter(Boolean).map(u => `<option>${u}</option>`).join("")}
                                </select>
                            </div>

                            <button class="btn btn-sm btn-primary" id="add-task-btn">‚ûï Add Task</button>
                        </div>
                        <h4 style="margin-bottom:10px;">Tasks List</h4>

                        <table class="table table-bordered table-hover">
                            <thead>
                                <tr>
                                    <th onclick="sortBy('l2_task_name')" style="cursor:pointer;">Task ‚¨ç</th>
                                    <th onclick="sortBy('sector')" style="cursor:pointer;">Sector ‚¨ç</th>
                                    <th onclick="sortBy('incharge')" style="cursor:pointer;">In Charge ‚¨ç</th>
                                    <th onclick="sortBy('status')" style="cursor:pointer;">Status ‚¨ç</th>
                                    <th style="width:160px;">Update</th>
                                </tr>
                            </thead>
                            <tbody>
                    `;

                    list.forEach(task => {
                        const can_update =
                            current_user === "Administrator" ||
                            (is_lead && task.sector === user_sector) ||
                            (task.incharge === current_user);

                        const badge_color =
                            task.status === "Completed" ? "#28a745" :
                            task.status === "In Progress" ? "#ffc107" :
                            task.status === "Delayed" ? "#dc3545" : "#0dcaf0";

                        html += `
                            <tr>
                                <td>${task.l2_task_name}</td>
                                <td>${task.sector}</td>

                                ${(is_lead || current_user === "Administrator")
                                    ? `<td>
                                            <select class="form-control incharge-dropdown"
                                                    data-task="${task.name}">
                                                <option value="">-- Select --</option>
                                                ${(data.sector_users[task.sector] || [])
                                                    .map(u => `<option value="${u.user}" ${task.incharge === u.user ? "selected" : ""}>${u.user}</option>`)
                                                    .join("")}
                                            </select>
                                      </td>`
                                    : `<td><span>${task.incharge || "-"}</span></td>`
                                }

                                <td>
                                    <span class="badge"
                                        style="background:${badge_color}; color:white;">
                                        ${task.status}
                                    </span>
                                </td>

                                <td>
                                    ${can_update
                                        ? `
                                            <select class="form-control task-status-dropdown"
                                                    data-task="${task.name}">
                                                <option ${task.status === "Pending" ? "selected" : ""}>Pending</option>
                                                <option ${task.status === "In Progress" ? "selected" : ""}>In Progress</option>
                                                <option ${task.status === "Completed" ? "selected" : ""}>Completed</option>
                                                <option ${task.status === "Delayed" ? "selected" : ""}>Delayed</option>
                                            </select>
                                          `
                                        : `<select class="form-control" disabled><option>${task.status}</option></select>`
                                    }
                                </td>
                            </tr>
                        `;
                    });

                    html += `</tbody></table>`;
                    frm.fields_dict.custom_tasks_overview.$wrapper.html(html);

                    document.getElementById("add-task-btn").onclick = () => open_add_task_modal(frm.doc.name);
                    document.getElementById("flt-status").onchange = e => { filter_status = e.target.value; applyFiltersAndRender(); };
                    document.getElementById("flt-sector").onchange = e => { filter_sector = e.target.value; applyFiltersAndRender(); };
                    document.getElementById("flt-incharge").onchange = e => { filter_incharge = e.target.value; applyFiltersAndRender(); };

                    // IN-CHARGE UPDATE
                    setTimeout(() => {
                        document.querySelectorAll(".incharge-dropdown").forEach(sel => {
                            sel.addEventListener("change", function () {
                                frappe.call({
                                    method: "psn_custom_rdb_app.psn_readiness_dashboard.event_logic.update_task_incharge",
                                    args: { task_name: this.dataset.task, user: this.value },
                                    callback() { cur_frm.reload_doc(); }
                                });
                            });
                        });
                    }, 200);

                    // STATUS UPDATE
                    setTimeout(() => {
                        document.querySelectorAll(".task-status-dropdown").forEach(sel => {
                            sel.addEventListener("change", function () {
                                frappe.call({
                                    method: "psn_custom_rdb_app.psn_readiness_dashboard.event_logic.update_event_task_status",
                                    args: { l2_task_name: this.dataset.task, status: this.value },
                                    callback() { cur_frm.reload_doc(); }
                                });
                            });
                        });
                    }, 200);
                }

                applyFiltersAndRender();
            }
        });
    }
});

//-------------------------------------------------------------
// ADD TASK MODAL
//-------------------------------------------------------------
window.open_add_task_modal = function(event_name) {
    const d = new frappe.ui.Dialog({
        title: "Add Event Task",
        fields: [
            { label: "Task Name", fieldname: "l2_task_name", fieldtype: "Data", reqd: 1 },
            { label: "Sector", fieldname: "sector", fieldtype: "Link", options: "Sector", reqd: 1 },
            { label: "User In Charge", fieldname: "incharge", fieldtype: "Link", options: "User" },
            { label: "Due Date", fieldname: "due_date", fieldtype: "Date" },
            { label: "Description", fieldname: "task_description", fieldtype: "Small Text" }
        ],
        primary_action_label: "Create Task",
        primary_action(values) {
            frappe.call({
                method:"psn_custom_rdb_app.psn_readiness_dashboard.event_logic.create_event_task_from_popup",
                args:{ event:event_name, ...values },
                callback() {
                    frappe.show_alert({ message:"Task Created", indicator:"green" });
                    d.hide(); cur_frm.reload_doc();
                }
            });
        }
    });
    d.show();
};

// ---- CREATE SECTOR ----
window.create_sector = function () {
    frappe.new_doc("Sector");
};

// ---- ADD SECTOR MEMBERS ----
window.add_sector_members = function () {
    let d = new frappe.ui.Dialog({
        title: "Add Members to Sector",
        fields: [
            {
                label: "Sector",
                fieldname: "sector",
                fieldtype: "Link",
                options: "Sector",
                reqd: 1
            },
            {
                label: "Members",
                fieldname: "members",
                fieldtype: "Table",
                cannot_add_rows: false,
                in_place_edit: true,
                fields: [
                    {
                        label: "User",
                        fieldname: "user",
                        fieldtype: "Link",
                        options: "User",
                        reqd: 1
                    },
                    {
                        label: "Is Sector Lead?",
                        fieldname: "is_sector_lead",
                        fieldtype: "Check"
                    }
                ]
            }
        ],
        primary_action_label: "Save Members",
        primary_action(values) {
            frappe.call({
                method: "psn_custom_rdb_app.psn_readiness_dashboard.event_logic.add_sector_members",
                args: values,
                callback: function () {
                    frappe.show_alert({ message: "Sector Members Updated", indicator: "green" });
                    d.hide();
                }
            });
        }
    });
    d.show();
};

// ---- CREATE EVENT READINESS ----
window.create_event_readiness = function () {
    frappe.new_doc("Event Readiness");
};
