script:
window.open_create_user_modal = function () {
    const d = new frappe.ui.Dialog({
        title: "Create User & Assign Sectors",
        fields: [
            {
                label: "Full Name",
                fieldname: "full_name",
                fieldtype: "Data",
                reqd: 1,
            },
            {
                label: "Email",
                fieldname: "email",
                fieldtype: "Data",
                reqd: 1,
            },
            {
                label: "Sectors",
                fieldname: "sectors",
                fieldtype: "Table",
                options: "User Sector Assignment",
                reqd: 1,
                in_place_edit: true,
                fields: [
                    {
                        fieldtype: "Link",
                        fieldname: "sector",
                        label: "Sector",
                        options: "Sector",
                        reqd: 1,
                        in_list_view: 1,
                    },
                    {
                        fieldtype: "Check",
                        fieldname: "is_sector_lead",
                        label: "Sector Lead?",
                        in_list_view: 1,
                    },
                ],
            },
        ],
        primary_action_label: "Create User",
        primary_action() {
            const values = d.get_values();
            console.log("Dialog values going to server:", values);

            frappe.call({
                method:
                    "psn_custom_rdb_app.psn_readiness_dashboard.event_logic.create_sector_user",
                args: {
                    full_name: values.full_name,
                    email: values.email,
                    sectors: values.sectors, // <-- array; will become JSON string server-side
                },
                callback() {
                    frappe.show_alert({
                        message: __("User Created Successfully"),
                        indicator: "green",
                    });
                    d.hide();
                },
                error(err) {
                    frappe.msgprint({
                        title: "Error",
                        message: err.message || "Unable to create user",
                        indicator: "red",
                    });
                },
            });
        },
    });

    d.show();
};

window.open_create_sector_modal = function () {
    let d = new frappe.ui.Dialog({
        title: "Create Sector",
        fields: [
            { label: "Sector Name", fieldname: "sector_name", fieldtype: "Data", reqd: 1 },
            { label: "Sector Lead", fieldname: "sector_lead", fieldtype: "Link", options: "User", reqd: 0 }
        ],
        primary_action_label: "Create Sector",
        primary_action(values) {
            frappe.call({
                method: "frappe.client.insert",
                args: {
                    doc: {
                        doctype: "Sector",
                        sector_name: values.sector_name,
                        sector_lead: values.sector_lead,
                    }
                },
                callback(r) {
                    frappe.show_alert("Sector Created ✔");
                    d.hide();
                    frappe.ui.toolbar.clear_cache();
                }
            });
        }
    });

    d.show();
};

window.open_create_event_modal = function () {
    let d = new frappe.ui.Dialog({
        title: "Create Event",
        fields: [
            {
                label: "Event Name",
                fieldname: "event_name",
                fieldtype: "Data",
                reqd: 1
            },
            {
                label: "Event Start Date",
                fieldname: "event_date",
                fieldtype: "Date",
                reqd: 1,
                description: "Must be at least 1 day after today"
            },
            {
                label: "Event End Date",
                fieldname: "custom_event_end_date",
                fieldtype: "Date",
                reqd: 1
            },
            {
                label: "Event Owner",
                fieldname: "event_owner",
                fieldtype: "Link",
                options: "User",
                reqd: 1
            },
            {
                label: "Event Sponsor",
                fieldname: "custom_event_sponsor",
                fieldtype: "Data",
                reqd: 1
            },
            {
                label: "Event Description",
                fieldname: "event_description",
                fieldtype: "Small Text",
                reqd: 1
            },
            {
                label: "Use Default Task Template",
                fieldname: "use_default_tasks",
                fieldtype: "Check",
                default: 1
            }
        ],
        primary_action_label: "Create Event",
        primary_action(values) {

            // -------------------------------
            // DATE VALIDATION
            // -------------------------------
            const today = new Date();
            const min_date = new Date(today.setDate(today.getDate() + 1));
            const startDate = new Date(values.event_start_date);
            const endDate = new Date(values.event_end_date);

            if (startDate < min_date) {
                frappe.msgprint({
                    title: "Invalid Start Date",
                    message: "Event Start Date must be at least 1 day after today.",
                    indicator: "red"
                });
                return;
            }

            if (endDate <= startDate) {
                frappe.msgprint({
                    title: "Invalid End Date",
                    message: "Event End Date must be greater than Event Start Date.",
                    indicator: "red"
                });
                return;
            }

            // -------------------------------
            // INSERT EVENT DOCUMENT
            // -------------------------------
            frappe.call({
                method: "frappe.client.insert",
                args: {
                    doc: {
                        doctype: "Event Readiness",
                        event_name: values.event_name,
                        event_date: values.event_date,
                        custom_event_end_date: values.custom_event_end_date,
                        event_owner: values.event_owner,
                        custom_event_sponsor: values.custom_event_sponsor,
                        event_description: values.event_description,
                        use_default_tasks: values.use_default_tasks ? 1 : 0
                    }
                },
                callback: function (r) {
                    frappe.show_alert({
                        message: __("Event Created"),
                        indicator: "green"
                    });
                    d.hide();

                    frappe.set_route("Form", "Event Readiness", r.message.name);
                }
            });
        }
    });

    d.show();
};

window.open_import_task_template_modal = function () {
    const d = new frappe.ui.Dialog({
        title: "Import Task Templates (CSV)",
        fields: [
            {
                fieldname: "csv_file",
                label: "CSV File",
                fieldtype: "Attach",
                reqd: 1,
                description: "Upload CSV with columns: sector, task_name, description, duration_days"
            }
        ],
        primary_action_label: "Import",
        primary_action(values) {
            if (!values.csv_file) {
                frappe.msgprint("Please upload a CSV file");
                return;
            }

            frappe.call({
                method: "psn_custom_rdb_app.psn_readiness_dashboard.event_logic.import_task_templates",
                args: {
                    csv_file: values.csv_file
                },
                freeze: true,
                freeze_message: "Importing Task Templates...",
                callback(r) {
                    const res = r.message || {};

                    let msg = `
                        <b>Import Summary</b><br><br>
                        ✅ Created: ${res.created || 0}<br>
                        ⚠️ Skipped: ${res.skipped || 0}<br>
                        ❌ Errors: ${(res.errors || []).length}
                    `;

                    if (res.errors && res.errors.length) {
                        msg += "<br><br><b>Error Details:</b><br>";
                        res.errors.forEach(err => {
                            msg += `Row ${err.row}: ${err.error}<br>`;
                        });
                    }

                    frappe.msgprint({
                        title: "Task Template Import Completed",
                        message: msg,
                        indicator: res.errors && res.errors.length ? "orange" : "green",
                        wide: true
                    });

                    d.hide();
                },
                error(err) {
                    frappe.msgprint({
                        title: "Import Failed",
                        message: err.message || "Unable to import CSV",
                        indicator: "red"
                    });
                }
            });
        }
    });

    d.show();
};

frappe.require([
    "https://cdn.jsdelivr.net/npm/fullcalendar@6.1.10/index.global.min.js"
], function () {

    function load_event_calendar() {
        const container = root_element.querySelector("#event-readiness-calendar");

        if (!container) {
            console.warn("Calendar container not ready. Retrying...");
            setTimeout(load_event_calendar, 300);
            return;
        }

        frappe.call({
            method: "frappe.client.get_list",
            args: {
                doctype: "Event Readiness",
                fields: ["name", "event_name", "event_date", "custom_event_end_date"]
            },
            callback(r) {
                const events = (r.message || []).map(doc => ({
                    id: doc.name,
                    title: doc.event_name,
                    start: doc.event_date,
                    end: doc.custom_event_end_date,
                    url: `/app/event-readiness/${doc.name}`
                }));

                const calendar = new FullCalendar.Calendar(container, {
                    initialView: "dayGridMonth",
                    height: 650,
                    events: events
                });

                calendar.render();
            }
        });
    }

    load_event_calendar();
});
